class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        all_zero = all(num == 0 for num in nums)
        if all_zero:
            return 0

        queries_per_j = [[] for _ in range(n)]
        for i in range(m):
            l, r, val = queries[i]
            for j in range(l, r + 1):
                queries_per_j[j].append((i, val))

        max_ij = -1

        for j in range(n):
            current_num = nums[j]
            if current_num == 0:
                continue

            available = queries_per_j[j]
            if not available:
                return -1

            target = current_num
            possible = {0}
            found = False
            current_max_i = -1

            for (query_idx, val) in available:
                new_possibles = set()
                for s in possible:
                    new_sum = s + val
                    if new_sum == target:
                        current_max_i = query_idx
                        found = True
                        break
                    elif new_sum < target:
                        new_possibles.add(new_sum)
                if found:
                    break
                possible.update(new_possibles)

            if not found:
                return -1

            if current_max_i > max_ij:
                max_ij = current_max_i

        if max_ij == -1:
            return 0
        else:
            return max_ij + 1

