class Solution {
private:
    int cal(const vector<int>& nums, const vector<vector<int>>& queries, int index) {
        if (nums[index] == 0) {
            return 0;
        }
        vector<bool> dp(nums[index] + 1, 0);
        dp[0] = 1;
        for (int i = 0; i < queries.size(); ++i) {
            if (queries[i][0] <= index && queries[i][1] >= index) {
                for (int j = dp.size() - 1; j >= queries[i][2]; --j) {
                    dp[j] = dp[j] | (dp[j - queries[i][2]]);
                    // cout << dp[j] << " ";
                }
                // cout << endl;
                if (dp.back()) {
                    return i + 1;
                }
            }
        }
        return -1;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ret = 0;
        for (int i = 0; i < nums.size(); ++i) {
            int res = cal(nums, queries, i);
            // cout << endl;
            // cout << i << " " << res << endl;
            if (res == -1) {
                return -1;
            }
            ret = max(ret, res);
        }
        return ret;
    }
};

