class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<bool>> memo(n,vector<bool>(1001));
        auto check = [&](int v) {
            for(int i = 0;i < n;i++)for(int j = 0;j < 1001;j++)memo[i][j] = false;
            for(int i = 0;i < n;i++)memo[i][0] = true;
            for(int i = 0;i < v;i++) {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                for(int j = l;j <= r;j++) {
                    for(int k = 1000;k >= val;k--) {
                        memo[j][k] = memo[j][k] || memo[j][k-val];
                    }
                }
            }
            for(int i = 0;i < n;i++) if(memo[i][nums[i]]==false) return false;
            return true;
        };
        int l = 0,r = m;
        while(l <= r) {
            int mid = (l+r)/2;
            if(check(mid)) {
                r = mid-1;
            } else {
                l = mid+1;
            }
        }
        return l>m?-1:l;
    }
};

