class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = INT_MAX, n = queries.size(), m = nums.size();
        unordered_map<int, vector<pair<int, int>>> h;
        for (int i = 0; i < n; ++ i) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = 0; j < m; ++ j) {
                auto& v = h[j];
                if (nums[j] == 0) {
                    v.push_back({0, val});
                    continue;
                }
                if (j >= l && j <= r) {
                    v.push_back({i + 1, val});
                }
            }
        }
        vector<int> ans(m, -1);
        for (int i = 0; i < m; ++ i) {
            int x = nums[i];
            auto& v = h[i];
            vector<vector<int>> dp(v.size() + 1, vector<int>(x + 1, 0));
            dp[0][0] = 1;
            for (int k = 1; k <= v.size(); ++ k) {
                for (int j = 0; j <= x; ++ j) {
                    if (j >= v[k - 1].second) {
                        dp[k][j] = max(dp[k - 1][j], dp[k - 1][j - v[k - 1].second]);
                    } else {
                        dp[k][j] = dp[k - 1][j];
                    }
                }
            }
            for (int k = 1; k <= v.size(); ++ k) {
                if (dp[k][x] == 1) {
                    ans[i] = v[k - 1].first;
                    break;
                }
            }
        }
        for (int i = 0; i < m; ++ i) {
            if (ans[i] == -1) {
                return -1;
            }
        }
        return ranges::max(ans);
    }
};

