class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        int dp[1001];
        for (int i = 0; i < nums.size(); ++ i) {
            if (nums[i] == 0) {
                res = std::max(res, 0);
            } else {
                memset(dp, 0, 4 * (1 + nums[i]));
                dp[0] = 1;
                int idx = 0;
                for (; idx < queries.size(); ++ idx) {
                    if (queries[idx][0] <= i && queries[idx][1] >= i) {
                        auto v= queries[idx][2];
                        for (int j = nums[i]; j >= v; -- j) {
                            if (dp[j - v] == 1) {
                                dp[j] = 1;
                            }
                        }
                        if (dp[nums[i]] == 1) {
                            break;
                        }
                    }
                }
                if (dp[nums[i]] == 1) {
                    res = std::max(res, idx + 1);
                } else return -1;
            }

        }
        return res;

    }
};

