class Solution {
    bool combineSum(vector<int>& nums, int target) {
        if (target == 0) return true;
        int n = nums.size();
        if (n == 0) return false;
        vector<vector<int>> f(n, vector<int>(target+1));
        if (nums[0] <= target) f[0][nums[0]] = 1;
        f[0][0] = 1;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= target; j++) {
                f[i][j] = f[i-1][j];
                if (nums[i] <= j) {
                    f[i][j] |= f[i-1][j-nums[i]]; 
                }
            }
        }
        // for (int i = 0; i < n; i++) {
        //     for (int j = 0; j <= target; j++) {
        //         cout << f[i][j] << " ";
        //     }
        //     cout << endl;
        // }
        return f[n-1][target];
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) { 
        bool zeros = true;
        for (int x : nums) {
            zeros = zeros && x == 0;
        }
        if (zeros) return 0;
        int n = nums.size(), m = queries.size();
        vector<vector<int>> ops(n);
        for (int i = 0; i < m; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) 
                if (nums[j] >= val) ops[j].push_back(val);
            bool allZero = true;
            for (int j = 0; j < n; j++) {
                allZero = allZero && combineSum(ops[j], nums[j]);
                // cout << "---------" << endl;
            }
            if (allZero) return i + 1;
        }
        return -1;
    }
};

