class Solution
{
    static int check(int const num, vector<int> const &vals, vector<vector<bool>> &dp)
    {
        if (num == 0) return 0;
        for (int j = 1; j <= num; ++j) dp[0][j] = false;
        dp[0][0] = true;
        for (int i = 1; i <= vals.size(); ++i)
        {
            int const val = vals[i - 1];
            for (int j = 0; j <= num; ++j)
                dp[i][j] = dp[i - 1][j] || j >= val && dp[i - 1][j - val];
            if (dp[i][num]) return i;
        }
        return -1;
    }

public:
    int minZeroArray(vector<int> const &nums, vector<vector<int>> const &queries)
    {
        vector<vector<int>> vals_possible_at_each_position(nums.size());
        for (vector<int> const &q : queries)
        {
            int const l = q[0], r = q[1], v = q[2];
            for (int i = 0; i != l; ++i) vals_possible_at_each_position[i].push_back(0);
            for (int i = l; i <= r; ++i) vals_possible_at_each_position[i].push_back(v);
            for (int i = r + 1; i != nums.size(); ++i) vals_possible_at_each_position[i].push_back(0);
        }
        int ans = -1;
        vector<vector<bool>> dp(queries.size() + 1, vector<bool>(*max_element(nums.cbegin(), nums.cend()) + 1, 0));
        for (int i = 0; i != nums.size(); ++i)
        {
            int const challenger = check(nums[i], vals_possible_at_each_position[i], dp);
            if (challenger < 0) return -1;
            if (ans < challenger) ans = challenger;
        }
        return ans;
    }
};

