type state struct {
	idx int
	val int
}

func minZeroArray(nums []int, queries [][]int) int {
	n := len(nums)
	posLists := make([][]state, n)
	for qi, query := range queries {
		l, r, val := query[0], query[1], query[2]
		for j := l; j <= r; j++ {
			posLists[j] = append(posLists[j], state{idx: qi, val: val})
		}
	}
	isValid := func(k int) bool {
		for j := 0; j < n; j++ {
			target := nums[j]
			if target == 0 {
				continue
			}
            //f[s]  s
			f := make([]bool, target+1)
			f[0] = true
            //jk
			for _, qinfo := range posLists[j] {
				if qinfo.idx < k {
					for s := target - qinfo.val; s >= 0; s-- {
						if f[s] && s+qinfo.val <= target {
							f[s+qinfo.val] = true
						}
					}
				}
			}
			if !f[target] {
				return false
			}
		}
		return true
	}
	lo, hi := 0, len(queries)+1
	for lo < hi {
		mid := (lo + hi) / 2
		if isValid(mid) {
			hi = mid
		} else {
			lo = mid + 1
		}
	}
	if lo > len(queries) {
		return -1
	}
	return lo
}

