class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        if all(x == 0 for x in nums):
            return 0

        dp = [set() for _ in range(n)]
        for i in range(n):
            dp[i].add(0)

        not_solved = [i for i in range(n)]

        for j in range(m):
            l, r, v = queries[j]
            solved_this_query = []
            for i in list(not_solved):
                if l <= i <= r:
                    newSums = set()
                    for s in dp[i]:
                        nxt = s + v
                        if nxt <= nums[i]:
                            newSums.add(nxt)
                    if newSums:
                        dp[i].update(newSums)
                    if nums[i] in dp[i]:
                        solved_this_query.append(i)
            for i in solved_this_query:
                not_solved.remove(i)
            if not not_solved:
                return j + 1
        return -1

