class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        m = len(queries)
        n = len(nums)

        #  k  i  nums[i]
        def feasible(k):
            for i in range(n):
                #  k  i 
                vals = [queries[j][2] for j in range(k) if queries[j][0] <= i <= queries[j][1]]
                target = nums[i]
                # dp[s]  s
                dp = [False] * (target + 1)
                dp[0] = True
                for v in vals:
                    # 0/1 
                    for s in range(target, v - 1, -1):
                        if dp[s - v]:
                            dp[s] = True
                if not dp[target]:
                    return False
            return True

        #  k (0 <= k <= m)  feasible(k)  True
        lo, hi = 0, m + 1
        while lo < hi:
            mid = (lo + hi) // 2
            if feasible(mid):
                hi = mid
            else:
                lo = mid + 1

        return lo if lo <= m else -1

