class Solution {
    using ll = long long;

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {

        ll n = nums.size(), m = q.size();
        auto check = [&](ll k) -> bool {
            vector<vector<ll>> c(n);
            for (ll j{}; j < k; ++j) {
                ll l = q[j][0], r = q[j][1], v = q[j][2];
                for (ll i = l; i <= r; ++i) {
                    c[i].emplace_back(v);
                }
            }

            for (ll i{}; i < n; ++i) {
                if (c[i].empty() && nums[i] != 0) {
                    return false;
                    continue;
                }
                vector<int> f(nums[i] + 1);
                f[0] = 1;
                for(auto ii : c[i])
                    {
                        for(ll j = nums[i]; j >= ii; j--)
                            {
                                if(f[j - ii])
                                    f[j] = 1;
                            }
                    }
                if(!f[nums[i]])
                    return false;
            }
            return true;
        };

        ll l = 0, r = m + 1;
        ll re = -1;
        while (l < r) {
            ll mid = l + (r - l) / 2;
            if (check(mid)) {
                re = mid;
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        if (re == -1 || re > m) {
            return -1;
        }
        return re;
    }
};

