class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        cover = [[] for _ in range(n)]
        for idx, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                cover[i].append((idx, val))

        for i in range(n):
            cover[i].sort()

        max_j_list = []
        for i in range(n):
            if nums[i] == 0:
                max_j_list.append(-1)
                continue

            target = nums[i]
            query_list = cover[i]
            dp = {0: -1}
            for j, val in query_list:
                temp = {}
                for s in list(dp.keys()):
                    current_max_j = dp[s]
                    new_s = s + val
                    if new_s > target:
                        continue
                    new_max_j = max(current_max_j, j)
                    if new_s in temp:
                        if new_max_j < temp[new_s]:
                            temp[new_s] = new_max_j
                    else:
                        temp[new_s] = new_max_j
                for s in temp:
                    if s not in dp or temp[s] < dp.get(s, float('inf')):
                        dp[s] = temp[s]

            if target not in dp:
                return -1
            max_j_list.append(dp[target])

        max_max_j = max(max_j_list)
        if max_max_j == -1:
            return 0
        else:
            return max_max_j + 1

