class Solution {
	public int minZeroArray(int[] nums, int[][] queries) {
		int resK[] = new int[nums.length];

		for (int i = 0 ; i < nums.length ; i++) {
			boolean f[] = new boolean[nums[i] + 1];
			f[0] = true;

			int res = doFind(f, queries, nums, i);
			resK[i] = res;
		}


		int maxK = resK[0];
		for (int res : resK) {
			if (res == -1) {
				return -1;
			}
			if (maxK < res) {
				maxK = res;
			}
		}
		return maxK;
	}

	private int doFind(boolean[] f, int[][] queries, int nums[], int i) {
		if (nums[i] == 0) {
			return 0;
		}
		for (int j = 0 ; j < queries.length ; j++) {
			int l = queries[j][0];
			int r = queries[j][1];
			int val = queries[j][2];

			if ((l <= i) && (i <= r)) {
				for (int k = nums[i] ; k >= 0 ; k--) {
					int p = k - val;
					if (p < 0) {
						break;
					}
					if (f[p]) {
						f[k] = true;
					}
				}

				if (f[nums[i]]) {
					return j + 1;
				}
			}
		}
		return -1;
	}
}

