class Solution {
    public int minZeroArray(int[] nums, int[][] que) {
        int n=nums.length,m=que.length,ans=0;
        boolean[] dp;
        for(int i=0;i<n;i++){
            if(nums[i]==0) continue;
            dp=new boolean[nums[i]+1];
            dp[0]=true;
            int j=0;
            for(;j<m;j++){
                if(que[j][1]<i||que[j][0]>i) continue;
                int p=que[j][2];
                for(int k=nums[i];k>=p;k--)
                    dp[k]|=dp[k-p];
                if(dp[nums[i]]){
                    break;
                }
            }
            if(j==m) return -1;
            ans=Math.max(j+1,ans);
        }
        return ans;
    }
}

