import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

class Solution {

    boolean dfs(ArrayList<Integer> list, int targe, int idx, Boolean[][] memo) {
        if (idx < 0) return targe == 0;
        if (memo[targe][idx] != null) return memo[targe][idx];
        boolean ans = false;
        if (targe >= list.get(idx)) {
            ans = ans || dfs(list, targe - list.get(idx), idx - 1, memo);
        }
        ans = ans || dfs(list, targe, idx - 1, memo);
        memo[targe][idx] = ans;
        return ans;
    }

    boolean check(ArrayList<Integer> list, int targe) {
        Boolean[][] memo = new Boolean[targe + 1][list.size()];
        return dfs(list, targe, list.size() - 1, memo);
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        int[][] add = new int[m][n];
        boolean F = true;
        for (int num : nums) {
            if (num != 0) {
                F = false;
                break;
            }
        }
        if (F) return 0;
        ArrayList<Integer>[] adds = new ArrayList[n];
        Arrays.setAll(adds, aux -> new ArrayList<>());
        for (int i = 0; i < queries.length; i++) {
            for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                adds[j].add(queries[i][2]);
            }
            boolean ans = true;
            for (int j = 0; j < nums.length; j++) {
                if (nums[j] == 0) continue;
                ans = (ans && check(adds[j], nums[j]));
            }
            if (ans) return i + 1;
        }
        return -1;
    }
}

