class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n=len(nums)

        def cal(x):
            cnt=[Counter() for _ in range(n)]

            for l,r,w in queries[:x]:
                for j in range(l,r+1):
                    cnt[j][w]+=1

            for i,v in enumerate(nums):
                arr=list(cnt[i].items())
                @cache
                def dp(i,x):
                    if not x:return True  
                    if i==len(arr):return False
                    for c in range(arr[i][1]+1):
                        if c*arr[i][0]>x:break
                        if dp(i+1,x-c*arr[i][0]):return True 
                    return False
                if not dp(0,v):return False
            return True 

        l,r=0,len(queries)
        while l<=r:
            mid=(l+r)>>1
            if cal(mid):r=mid-1
            else:l=mid+1
        r+=1
        if r>len(queries):return -1
        return r

