class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        j_queries = [[] for _ in range(n)]


        for i in range(m):
            l, r, val = queries[i]
            for j in range(l, r + 1):
                j_queries[j].append((i, val))


        for j in range(n):
            j_queries[j].sort()

        max_k_list = []
        for j in range(n):
            target = nums[j]
            if target == 0:
                max_k_list.append(-1)
                continue

            dp = {0: -1}  # 
            covered_queries = j_queries[j]
            for (i, val) in covered_queries:
                tmp = {}
                # 
                for s in list(dp.keys()):
                    current_max = dp[s]
                    new_s = s + val
                    new_max = max(current_max, i)
                    if new_s > target:
                        continue
                    # tmp
                    if new_s not in tmp or new_max < tmp.get(new_s, float('inf')):
                        tmp[new_s] = new_max
                # tmpdp
                for s in tmp:
                    if s not in dp or tmp[s] < dp[s]:
                        dp[s] = tmp[s]

            if target not in dp:
                return -1
            else:
                max_k_list.append(dp[target])

        # 
        max_needed = -1
        for x in max_k_list:
            if x == -1:
                continue
            if x > max_needed:
                max_needed = x

        if max_needed == -1:
            # 
            if all(x == 0 for x in nums):
                return 0
            else:
                return -1
        else:
            return max_needed + 1

