class Solution {
    fun minZeroArray(nums: IntArray, queries: Array<IntArray>): Int {
        // queries[i] = [li, ri, vali]
        //     nums  [li, ri] 
        //          vali
        //         
        //             
        //             - 
        //                 dp(numsi, queriesi, less)
        //                     if (less < 0)
        //                         return
        //                     if (less == 0)
        //                         res = min(res, queriesi)
        //                     if (numsi in queries[queriesi][0]..queries[queriesi][1])
        //                         dp(numsi, queriesi + 1, less - queries[queriesi][2])
        //                     dp(numsi, queriesi + 1, less)
        //   0

        // 1 <= nums.length <= 10
        // 1 <= queries.length <= 1000
        // n=1000O(n 2 logn)
        // n  queriesnlogn 

        fun dp(numsi: Int, numsless: Int, queriesi: Int): Int {
            var res = Int.MAX_VALUE
            val cache = HashMap<Pair<Int, Int>, Boolean>()
            fun innerdp(numsi: Int, numsless: Int, queriesi: Int) {
                if (numsless == 0) {
                    res = min(res, queriesi)
                    return
                }
                if (queriesi >= queries.size)
                    return
                if (numsless to queriesi in cache)
                    return
                cache[numsless to queriesi] = true
                if (numsi >= queries[queriesi][0] 
                    && numsi <= queries[queriesi][1]
                    && numsless >= queries[queriesi][2])
                    innerdp(numsi, numsless - queries[queriesi][2], queriesi + 1)
                innerdp(numsi, numsless, queriesi + 1)
            }
            innerdp(numsi, numsless, queriesi)
            return res
        }
        var ans = 0

        for (i in nums.indices) {
            val k = dp(i, nums[i], 0)
            if (k == Int.MAX_VALUE)
                return -1
            ans = max(ans, k)
        }
        return ans
    }
}

