            for (int i = 0; i < n; i++) {
                auto& t = g2[i];
                for (auto& k : g[i]) {
                    for (int d = 1000; d >= 0; d--) {
                        if (!t[d]) continue;
                        if (k + d <= 1000) {
                            t[k + d] = 1;
