class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
         int n = nums.length;
        List<List<int[]>> coverage = new ArrayList<>();

        for (int i = 0; i < n; i++) {
            List<int[]> list = new ArrayList<>();
            for (int j = 0; j < queries.length; j++) {
                int[] q = queries[j];
                int l = q[0], r = q[1], val = q[2];
                if (i >= l && i <= r) {
                    list.add(new int[]{j, val});
                }
            }
            list.sort((a, b) -> a[0] - b[0]);
            coverage.add(list);
        }

        int maxK = 0;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }
            List<int[]> list = coverage.get(i);
            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);
            for (int[] q : list) {
                int jIndex = q[0];
                int val = q[1];
                Map<Integer, Integer> temp = new HashMap<>();
                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int s = entry.getKey();
                    int currentMax = entry.getValue();
                    int newSum = s + val;
                    int newMax = Math.max(currentMax, jIndex);
                    if (!temp.containsKey(newSum) || newMax < temp.get(newSum)) {
                        temp.put(newSum, newMax);
                    }
                }
                for (Map.Entry<Integer, Integer> entry : temp.entrySet()) {
                    int s = entry.getKey();
                    int m = entry.getValue();
                    if (!dp.containsKey(s) || m < dp.get(s)) {
                        dp.put(s, m);
                    }
                }
            }
            if (!dp.containsKey(target)) {
                return -1;
            }
            int currentMax = dp.get(target);
            maxK = Math.max(maxK, currentMax + 1);
        }
        return maxK;
    }
}

