class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res=0;
        for(int i = 0; i < nums.size(); i++){
            int tmpRes = calRes(queries, i, nums[i]);
            // cout << tmpRes << endl;
            res = max(res, tmpRes);
        }
        if(res==queries.size()+1) return -1;
        return res;
    }
    int calRes(vector<vector<int>>& queries, int ind, int val){
        if(val==0) return 0;
        vector<bool> dp(val+1,false);
        dp[val]=true;
        for(int i = 0; i < queries.size(); i++){
            auto query = queries[i];
            if(ind<query[0]||ind>query[1]) continue;
            vector<bool> tmp(val+1, false);
            for(int j = 0; j < dp.size(); j++){
                if(dp[j]==false) continue;
                tmp[j] = true;
                if(j>=query[2]){
                    tmp[j-query[2]]=true;
                    if(j==query[2]) return i+1;
                }
            }
            dp = tmp;
        }
        return queries.size()+1;
    }
};

