            if(nums[i]==0) continue;
            int tmp=INT_MAX;
            unordered_set<int>st;
            vector<int>arr;
            arr.emplace_back(nums[i]);
            st.emplace(nums[i]);
            for(int j=0;j<m;j++){
                if(rem[i][j]!=0){
                    if(st.count(rem[i][j])){
                        tmp=j+1;
                        break;
                    }
                    for(int k=arr.size()-1;k>=0;k--){
                        int nw=arr[k]-rem[i][j];
                        if(nw>0&&!st.count(nw)){
                            arr.emplace_back(nw);
                            st.emplace(nw);
                        }
                    }
                }
            }
            ans=max(ans,tmp);
        }
        return ans==INT_MAX?-1:ans;
    }
};

