class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int le = 0, ri = q.size(), md, sz = nums.size();
        vector<bool> dp(1007, true);
        auto check = [&]() -> bool {
            for (int loc = 0; loc < sz; ++ loc) {
                int &it = nums[loc];
                if (!it) continue;
                int m = it;
                int n = md;
                for (int i = 1; i <= m; ++ i) dp[i] = false;
                for (int i = 0; i < n; ++ i) {
                    int &l = q[i][0], &r = q[i][1], &val = q[i][2];
                    if (l > loc || loc > r) continue;
                    for (int j = m - val; j >= 0; -- j) {
                        if (dp[j]) dp[j + val] = true;
                    }
                }
                if (!dp[m]) return false;
            }
            return true;
        };
        while (le < ri) {
            md = le + ri >> 1;
            if (check()) ri = md;
            else le = md + 1;
        }
        md = le;
        return check() ? le : -1;
    }
};

