class Solution:
    def minZeroArray(self, nums: list[int], queries: list[list[int]]) -> int:
        if max(nums) == 0: return 0

        def able(caps, num):
            n = len(caps)
            tmp = set()
            tmp.add(0)
            for i in range(n):
                add = set()
                for dt in tmp:
                    if dt + caps[i] == num:
                        return i + 1
                    if dt + caps[i] < num:
                        add.add(dt + caps[i])
                for dt in add:
                    tmp.add(dt)
            return -1

        res = 0
        for idx,num in enumerate(nums):
            if num == 0: continue
            tmp = []
            for qr in queries:
                if qr[0] <= idx <= qr[1]:
                    tmp.append(qr[2])
                else:
                    tmp.append(0)
            tt = able(tmp, num)
            if tt == -1: return -1
            res = max(res, tt)              

        return res

