    def minZeroArray(self, nums: list[int], queries: list[list[int]]) -> int:
        n = len(nums)
        m = len(queries)
        c = [[[] for i in range(12)] for j in range(m + 1)]
        for i in range(m):
            for j in range(n):
                c[i + 1][j] = c[i][j].copy()
            l, r, val = queries[i]
            for j in range(l, r + 1):
                c[i + 1][j].append(val)

        # for i in range(m + 1):
        #     for j in range(n):
        #         print(i,c[i][j])

        def check(arr, val):
            target = val
            f = 1
            for v in arr:
                if target >= v:
                    f |= (f & ((1 << (target - v + 1)) - 1)) << v
            return f >> target & 1 == 1 

        for i in range(m + 1):
            l, r, _ = queries[i - 1]
            fl = True
            for j in range(n):
                x = nums[j]
                arr = c[i][j]
                # print(i,arr)
                if not check(arr, x):
                    fl = False
                    break
            if fl:
                return i
        return -1

