class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<vector<int>> cover(n);

        // i
        for (int j = 0; j < q; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            for (int i = l; i <= r; ++i) {
                cover[i].push_back(j);
            }
        }

        int max_k = 0;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) continue;

            vector<int>& q_list = cover[i];
            if (q_list.empty()) return -1;

            // val
            int total_val = 0;
            for (int j : q_list) {
                total_val += queries[j][2];
            }
            if (total_val < target) return -1;

            // sum
            unordered_set<int> possible_sums;
            possible_sums.insert(0);
            int found_j = -1;

            for (size_t idx = 0; idx < q_list.size(); ++idx) {
                int j = q_list[idx];
                int val = queries[j][2];
                unordered_set<int> new_sums;

                bool found = false;
                for (int s : possible_sums) {
                    int new_s = s + val;
                    if (new_s == target) {
                        found_j = j;
                        found = true;
                        break;
                    } else if (new_s < target) {
                        new_sums.insert(new_s);
                    }
                }
                if (found) break;

                // sumpossible_sums
                for (int s : new_sums) {
                    possible_sums.insert(s);
                }
            }

            if (found_j == -1) return -1;
            if (found_j + 1 > max_k) {
                max_k = found_j + 1;
            }
        }

        return (max_k == 0) ? 0 : (max_k <= q ? max_k : -1);
    }
};

