struct Event {
    int j;
    bool is_start;
    Event(int j, bool is_start) : j(j), is_start(is_start) {}
};
class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        // 
        vector<vector<Event>> diff_events(n + 2); // 0n
        for (int j = 0; j < m; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            // 
            if (l < n) {
                diff_events[l].emplace_back(j, true);
            }
            int end_pos = r + 1;
            if (end_pos < n) {
                diff_events[end_pos].emplace_back(j, false);
            }
        }

        vector<vector<int>> element_queries(n);
        set<int> active;
        for (int i = 0; i < n; ++i) {
            // i
            for (auto& event : diff_events[i]) {
                int j = event.j;
                if (event.is_start) {
                    active.insert(j);
                } else {
                    active.erase(j);
                }
            }
            // activejelement_queries[i]
            element_queries[i].assign(active.begin(), active.end());
        }

        int max_k = -1;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) {
                continue; // k_i-1max_k
            }

            vector<int>& qjs = element_queries[i];
            unordered_map<int, int> dp;
            dp[0] = -1;
            for (int j : qjs) {
                int val = queries[j][2];
                // dp
                auto temp_dp = dp;
                for (auto& [s, current_j] : temp_dp) {
                    int new_s = s + val;
                    int new_j = max(current_j, j);
                    if (!dp.count(new_s) || new_j < dp[new_s]) {
                        dp[new_s] = new_j;
                    }
                }
            }
            if (!dp.count(target)) {
                return -1;
            }
            int k_i = dp[target];
            if (k_i > max_k) {
                max_k = k_i;
            }
        }

        if (max_k == -1) {
            // 0
            return 0;
        } else if (max_k >= m) {
            return -1;
        } else {
            return max_k + 1;
        }
    }
};

