def subset_sum(nums, target):
    dp = [False] * (target + 1)
    dp[0] = True
    for num in nums:
        for j in range(target, num - 1, -1):
            if dp[j - num]:
                dp[j] = True
    return dp[target]

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        idx_map = {}
        for i in range(n):
            idx_map[i] = []

        def check():
            for idx, arr in idx_map.items():
                t = nums[idx]
                if not subset_sum(arr, t):
                    return False
            return True

        if check():
            return 0

        for i, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                idx_map[j].append(v)
            if check():
                return i + 1
        return -1

