class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def solve(x,a):
            # x
            # print(f"solving x={x} a={a}")
            if x==0:
                return 0
            ma={}
            for qid,d in a:
                if d>x:
                    continue
                oldkeys=list(ma.keys())
                for i in oldkeys:
                    if i+d<=x:                        
                        ma[i+d]=1
                ma[d]=1
                # print("qid=",qid,ma)
                if x in ma:
                    return qid
            return 1e9
        if len(queries)==0 and max(nums)>0:
            return -1
        if max(nums)==0:
            return 0
        ans=0
        for i,v in enumerate(nums):
            if v==0:
                continue
            a=[(qid,d) for qid,(l,r,d) in enumerate(queries) if l<=i and r>=i]
            now=solve(v,a)
            # print(f"i={i} v={v} now={now}")
            ans=max(ans,now)
        if ans>=len(queries):
            return -1
        return ans+1

