class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        queries_per_element = [[] for _ in range(n)]

        for idx, (l, r, val) in enumerate(queries):
            if val == 0:
                continue
            l = max(l, 0)
            r = min(r, n - 1)
            for j in range(l, r + 1):
                queries_per_element[j].append((idx, val))

        for j in range(n):
            queries_per_element[j].sort()

        max_k = -1
        for j in range(n):
            target = nums[j]
            if target < 0:
                return -1
            if target == 0:
                continue

            q_list = queries_per_element[j]
            sum_val = sum(val for _, val in q_list)
            if sum_val < target:
                return -1

            dp = {0: -1}
            for i, val in q_list:
                new_dp = {}
                for s in dp:
                    current_max = dp[s]
                    if s in new_dp:
                        if current_max < new_dp[s]:
                            new_dp[s] = current_max
                    else:
                        new_dp[s] = current_max

                    s_new = s + val
                    if s_new > target:
                        continue
                    new_max = max(current_max, i)
                    if s_new in new_dp:
                        if new_max < new_dp[s_new]:
                            new_dp[s_new] = new_max
                    else:
                        new_dp[s_new] = new_max
                dp = new_dp

            if target not in dp:
                return -1
            k_j = dp[target]
            if k_j > max_k:
                max_k = k_j

        if max_k == -1:
            return 0
        else:
            return max_k + 1 if max_k + 1 <= len(queries) else -1

