class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        auto check = [&](int k) -> bool {
            for (int i = 0; i < n; i ++) {
                int target = nums[i];
                vector<bool> dp(target + 1, false);
                dp[0] = true;
                for (int j = 0; j < k; j ++) {
                    int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                    if (i < l || i > r) continue;
                    for (int s = target - val; s >= 0; s --) {
                        if (dp[s]) {
                            dp[s + val] = true;
                        }
                    }
                }
                if (!dp[target]) return false;
            }
            return true;
        };

        int l = 0, r = m;
        while(l < r) {
            int mid = (l + r) >> 1;
            if(check(mid))
                r = mid;
            else 
                l = mid + 1;
        }
        return check(l) ? l : -1;
    }
};

