class Solution {
public:
    static int minZeroArray(const vector<int> &nums, const vector<vector<int>> &queries) {
        if (ranges::all_of(nums, [](const int &x) { return x == 0; })) return 0;

        const int n = static_cast<int>(nums.size());
        int unDone = n;
        vector<char> done(n);
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                --unDone;
                done[i] = 1;
            }
        }

        vector<unordered_set<int>> sums(n, {0});
        for (int i = 0; i < queries.size(); ++i) {
            const int v = queries[i][2];

            for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                if (done[j] || v > nums[j]) continue;

                if (sums[j].contains(nums[j] - v)) {
                    --unDone;
                    done[j] = 1;
                    continue;
                }

                vector<int> reach;
                for (const int sum: sums[j]) {
                    if (sum < nums[j] - v) reach.push_back(sum + v);
                }
                sums[j].insert(reach.begin(), reach.end());
            }

            if (unDone == 0) return i + 1;
        }

        return -1;
    }
};

