class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        h = {}
        b = []
        for i in range(len(nums)):
            h[i] = {-nums[i]}
            b.append(True if -nums[i] == 0 else False)
        if all(b):
            return 0
        # print(h)
        for k, q in enumerate(queries):
            l1 = q[0]
            r1 = q[1]
            val1 = q[2]
            for i in range(l1, r1+1):
                # print(i)
                new_set = {x + val1 for x in h[i]}
                h[i] |= new_set
                if 0 in h[i]:
                    b[i] = True
            # print(h)
            if all(b):
                return k+1
        return -1

