class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int ans = 0;
        bool f = true;
        for(int i = 0; i < n; ++i) {
            if(nums[i] != 0) {
                f = false;
                break;
            }
        }
        if(f) return 0;
        for(int i = 0; i < n; ++i) {
            vector<bool> dp(1001);
            dp[0] = true;
            bool f = false;
            for(int j = 0; j < m; ++j) {
                if (queries[j][0] <= i && queries[j][1] >= i)
                {
                    for (int k = 1000 - queries[j][2]; k >= 0; --k)
                    {
                        dp[k + queries[j][2]] = dp[k + queries[j][2]] | dp[k];
                        if(dp[nums[i]]) break;
                    }
                }
                if(dp[nums[i]]) {
                    f = true;
                    ans = max(ans, j + 1);
                    break;
                }
            }
            if(!f) return -1;
        }
        return ans;
    }
};

