class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int[] diff = new int[n];
        Set<Integer>[] temp = new Set[n];
        int sum = 0;
        for (int i = 0; i < n; i++) {
            temp[i] = new HashSet<>();
            temp[i].add(0);
            sum += nums[i];
        }
        if (sum == 0) {
            return 0;
        }

        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for (int j = l; j <= r; j++) {
                List<Integer> path = new ArrayList<>();
                for (int k : temp[j]) {
                    path.add(k + val);
                }
                path.add(val);
                for (int k : path) {
                    temp[j].add(k);
                }
            }
            boolean flag = false;
            for (int j = 0; j < n; j++) {
                flag = false;
                for (int k : temp[j]) {
                    if (k == nums[j]) {
                        flag = true;
                        break;
                    }
                }
                if (flag == false) {
                    break;
                }
            }
            if(flag) {
                return i+1;
            }
        }

        return -1;
    }
}

