def earliest_element(n, arr):
    if n==0:
        return 0
    current_remains = {n}
    for index, num in enumerate(arr):
        new_remains = set()
        found = False
        for s in current_remains:
            # 
            new_remains.add(s)
            # 
            if s >= num:
                new_s = s - num
                if new_s == 0:
                    found = True
                new_remains.add(new_s)
        if found:
            return index+1
        current_remains = new_remains
    return -1

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        matrix = [[0 for _ in range(m)] for _ in range(n)]
        for j in range(m):
            for i in range(queries[j][0],queries[j][1]+1):
                matrix[i][j] = queries[j][2]
        ans = []
        for i in range(n):
            #print(nums[i],matrix[i])
            tmp = earliest_element(nums[i],matrix[i])
            if tmp == -1:
                return -1
            ans.append(tmp)

        return max(ans)

