class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = queries.size(), m = nums.size();
        int ans = -1;
        for (int k = 0; k < m; k++) {
            int x = nums[k];
            if (x == 0) {
                ans = max(ans, 0);
                continue;
            }

            vector<bool> f(x + 1, false);
            f[x] = true;
            for (int i = 0; i < n; i++) {
                int l = queries[i][0], r = queries[i][1], val =queries[i][2];
                if (k < l || k > r) continue;

                for (int j = val; j <= x; j++) {
                    f[j - val] = f[j] || f[j - val];
                }
                if (f[0] == true) {
                    ans = max(ans, i + 1);
                    break;
                }
            }
            if (f[0] == false) return -1;
        }
        return ans;
    }
};

