class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        m = len(queries)
        n = len(nums)
        queries_for = [[] for _ in range(n)]
        for idx, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                queries_for[i].append((val, idx))
        max_k = 0
        for i in range(n):
            target = nums[i]
            if target == 0:
                continue  
            q_list = queries_for[i]
            if not q_list and target != 0:
                return -1 
            INF = float('inf')
            dp = [INF] * (target + 1)
            dp[0] = -1 
            for val, idx in q_list:
                for s in range(target, -1, -1):
                    if dp[s] != INF:
                        new_s = s + val
                        if new_s > target:
                            continue
                        candidate_max = max(dp[s], idx)
                        if candidate_max < dp[new_s]:
                            dp[new_s] = candidate_max

            if dp[target] == INF:
                return -1
            current_k = dp[target] + 1
            if current_k > max_k:
                max_k = current_k


        if max_k > m:
            return -1
        return max_k if max_k != 0 else 0

