class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int sum=0;
        for(int i=0;i<n;i++)
            {
                if(nums[i]>0)
                    sum++;
            }
        int m=queries.size();
        if(sum==0)
            return 0;
        vector<vector<pair<int,int>>> v(n);
        for(int i=0;i<n;i++)
            {
                for(int j=0;j<m;j++)
                    {
                        int l=queries[j][0];
                        int r=queries[j][1];
                        int val=queries[j][2];
                        if(i<=r&&i>=l)
                        {
                            v[i].push_back({val,j});
                        }
                    }
            }
        vector<int> dp;
        vector<int> ans(n,-1);
        for(int i=0;i<n;i++)
            {
                if(nums[i]>0)
                {
                    dp.resize(nums[i]+1,0);
                    dp[0]=1;
                    for(int j=1;j<=nums[i];j++)
                        {
                            dp[j]=0;
                        }
                    for(auto [val,j]:v[i])
                        {
                            for(int t=nums[i];t>=val;t--)
                                {
                                    dp[t]=max(dp[t],dp[t-val]);
                                }
                            if(dp[nums[i]]>0)
                            {
                                ans[i]=j;
                                break;
                            }
                        }
                    if(ans[i]==-1)
                        return -1;
                }
                else
                {
                    ans[i]=0;
                }
            }
        int ma=0;
        for(int i=0;i<n;i++)
            {
                ma=max(ma,ans[i]);
            }
        return ma+1;
    }
};

