class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
 int n = nums.size();
        int q = queries.size();

        vector<vector<pair<int, int>>> positionQueries(n);
        for (int i = 0; i < q; ++i) {
            int left = queries[i][0];
            int right = queries[i][1];
            int value = queries[i][2];
            for (int j = left; j <= right; ++j) {
                positionQueries[j].emplace_back(value, i);
            }
        }

        int result = -1;
        for (int i = 0; i < n; ++i) {
            int targetValue = nums[i];
            if (targetValue == 0) {
                result =max(result, 0);
                continue;
            }

            unordered_map<int, int> dp;
            dp[0] = -1;

            for (const auto& query : positionQueries[i]) {
                int val = query.first;
                int index = query.second;
                unordered_map<int, int> newDp = dp;
                for (const auto& [sum, maxIndex] : dp) {
                    int newSum = sum + val;
                    if (newSum > targetValue) continue;
                    int newMaxIndex = max(maxIndex, index);
                    if (newDp.find(newSum) == newDp.end() || newMaxIndex < newDp[newSum]) {
                        newDp[newSum] = newMaxIndex;
                    }
                }
                dp = newDp;
            }

            if (dp.find(targetValue) == dp.end()) {
                return -1;
            }

            int maxQueryIndex = dp[targetValue];
            int currentK = maxQueryIndex + 1;
            result = max(result, currentK);
        }

        return result;
    }
};

