class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int zero = 0;
        Set<Integer>[] sets = new HashSet[n];
        boolean[] visit = new boolean[n];
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                visit[i] = true;
                zero++;
            }
            sets[i] = new HashSet<>();
        }
        if (zero == n) {
            return 0;
        }
        for (int i = 0; i < queries.length; i++) {
            int[] query = queries[i];
            for (int j = query[0]; j <= query[1]; j++) {
                if (visit[j]) {
                    continue;
                }
                if (nums[j] < query[2]) {
                    continue;
                }
                if (nums[j] == query[2] || sets[j].contains(query[2])) {
                    visit[j] = true;
                    zero++;
                    if (zero == n) {
                        return i + 1;
                    }
                    continue;
                }
                List<Integer> addList = new ArrayList<>();
                addList.add(nums[j] - query[2]);
                for (int pre : sets[j]) {
                    if (pre < query[2]) {
                        continue;
                    }
                    addList.add(pre - query[2]);
                }
                sets[j].addAll(addList);
            }
        }
        return -1;
    }
}

