class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), m = q.size();
        vector<vector<int>> f(n,vector<int>(1001,0));
        int cnt = 0;
        for(int i = 0;i < n;i++){
            f[i][0] = 1;
            cnt += nums[i] == 0;
        }
        if(cnt == n) return 0;

        for(int i = 0;i < m;i++){
            int l = q[i][0];
            int r = q[i][1];
            int v = q[i][2];
            for(int j = l;j <= r;j++){
                vector<int> &f2 = f[j];
                for(int k = nums[j];k >= v;k--){
                    f2[k] |= f2[k - v];
                }
            }
            cnt = 0;
            for(int j = 0;j < n;j++){
                if(f[j][nums[j]])++cnt;
            }
            if(cnt == n) return i + 1;
        }
        return -1;
    }
};

