from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        all_j_max = []
        for i in range(len(nums)):
            x = nums[i]
            if x == 0:
                all_j_max.append(-1)
                continue
            dp = {0: -1}
            for j in range(len(queries)):
                l, r, val = queries[j]
                if l > i or r < i:
                    continue
                tmp = {}
                for s in list(dp.keys()):
                    current_j_max = dp[s]
                    new_s = s + val
                    if new_s > x:
                        continue
                    new_j_max = max(current_j_max, j)
                    if new_s in tmp:
                        if new_j_max < tmp[new_s]:
                            tmp[new_s] = new_j_max
                    else:
                        tmp[new_s] = new_j_max
                for s in tmp:
                    if s in dp:
                        if tmp[s] < dp[s]:
                            dp[s] = tmp[s]
                    else:
                        dp[s] = tmp[s]
            if x not in dp:
                return -1
            all_j_max.append(dp[x])

        max_j = max(all_j_max)
        if max_j == -1:
            return 0
        return max_j + 1

