class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0
        n, m = len(nums), len(queries)
        ans = [inf] * n
        dic = [set([0]) for _ in range(n)]
        for i, x in enumerate(nums):
            if x == 0:
                ans[i] = 0
        for q, (l, r, v) in enumerate(queries):
            for i in range(l, r+1):
                if ans[i] == inf:
                    x = nums[i]
                    tmp = set()
                    for s in dic[i]:
                        if s + v <= x:
                            tmp.add(s + v)
                    dic[i] |= tmp
                    if x in dic[i]:
                        ans[i] = q
        mx = max(ans)
        return -1 if mx == inf else mx + 1

