class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        zero = [False] * (pow(2, math.ceil(math.log2(len(nums))) + 1)+1)
        options = defaultdict(set)

        def build(s: int, t: int, p: int):
            if s == t:
                if nums[s-1] == 0:
                    zero[p] = True
                else:
                    options[s-1].add(0)
                return
            m = s + ((t - s) >> 1)
            build(s, m, p*2)
            build(m+1, t, p*2+1)
            zero[p] = zero[p*2] and zero[p*2+1]
        build(1, len(nums), 1)

        def update(l: int, r: int, s: int, t: int, p: int, n: int):
            if zero[p]:
                return
            if s == t:
                num = nums[s-1]
                tmp = options[s-1].copy()
                for op in tmp:
                    options[s-1].add(op+n)
                if num in options[s-1]:
                    zero[p] = True
                return
            m = s + ((t - s) >> 1)
            if l <= m:
                update(l, min(r, m), s, m, p*2, n)
            if m < r:
                update(max(l, m+1), r, m+1, t, p*2+1, n)
            zero[p] = zero[p*2] and zero[p*2+1]

        if zero[1]:
            return 0
        k = 0
        for s, t, n in queries:
            update(s+1, t+1, 1, len(nums), 1, n)
            k += 1
            if zero[1]:
                return k
        return -1

