            for(int j=l;j<=r;j++){
                if(can[j].empty()) continue;
                auto m =can[j];
                for(int n:m){
                    if(n-val>0){
                        can[j].insert(n-val);
                    }else if(n==val){
                        can[j].clear();
                        break;
                    }
                }
            }


            for(int k=0;k<nums.size();k++){
                if(!can[k].empty()) break;
                if(k==nums.size()-1) return i+1;
            }
        }
        return -1;
    }
};

