class Solution {
public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        boolean[][] dp = new boolean[n][1001];
        boolean falg = true;

        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
            if (nums[i] != 0) {
                falg = false;
            }
        }
        if (falg) {
            return 0;
        }
        for (int k = 0; k < queries.length; k++) {
            int[] query = queries[k];
            falg = true;
            for (int i = 0; i < n; i++) {
                int target = nums[i];
                if (dp[i][target] || i < query[0] || i > query[1]) {
                    falg &= dp[i][target];
                    continue;
                }
                for (int j = target; j >= query[2]; j--) {
                    dp[i][j] |= dp[i][j - query[2]];
                }
                falg &= dp[i][target];
            }
            if (falg) {
                return k + 1;
            }
        }

        return -1;
    }
}

