class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)

        all_zero = True
        for num in nums:
            if num != 0:
                all_zero = False
                break

        if all_zero:
            return 0

        def check(k: int) -> bool:
            idx_to_vals = defaultdict(list)
            for i in range(0, k + 1):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    idx_to_vals[j].append(val)

            for j in range(0, n):
                vals = idx_to_vals[j]
                len_vals = len(vals)
                dp = [[False for _ in range(nums[j] + 1)] for _ in range(len_vals + 1)]
                dp[0][0] = True
                for a in range(1, len_vals + 1):
                    for b in range(0, nums[j] + 1):
                        dp[a][b] |= dp[a - 1][b]
                        if b - vals[a - 1] >= 0:
                            dp[a][b] |= dp[a - 1][b - vals[a - 1]]

                if not dp[len_vals][nums[j]]:
                    return False

            return True

        ans = -1
        left, right = 0, m - 1
        while left <= right:
            mid = (left + right) >> 1
            if check(mid):
                ans = mid + 1
                right = mid - 1
            else:
                left = mid + 1

        return ans

