class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n=a.size(),m=q.size();
        bool st=0;
        for(int i=0;i<n;i++) if(a[i]) st=1;
        if(!st) return 0;


        auto check=[&](int x)->bool{
            // int dp[n+1][1010];
            vector<vector<int>>dp(n+1,vector<int>(1010));
            for(int i=0;i<n;i++) dp[i][0]=1;

            for(int i=0;i<x;i++){
                int ll=q[i][0],rr=q[i][1],xx=q[i][2];
                // cout<<ll<<' '<<rr<<' '<<xx<<endl;
                for(int j=ll;j<=rr;j++){
                    for(int k=a[j];k>=xx;k--){
                        dp[j][k]=max(dp[j][k],dp[j][k-xx]);
                    }
                }
            }
            for(int i=0;i<n;i++){
                // cout<<x<<' '<<dp[0][i]<<endl;
                if(!dp[i][a[i]]) return 0;
            }
            return 1;
        };
        int l=0,r=m+1;
        // return check(2);
        while(l<r){
            int mid=l+r>>1;
            if(check(mid)) r=mid;
            else l=mid+1;
        }
        // return l;
        if(l==m+1) return -1;
        else return l;
    }
};

