class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int sum = 0;
        for (int i = 0; i < n; ++i){
            sum += nums[i];
        }
        if (sum == 0){
            return 0;
        }
        auto check = [&](int k){
            int g = 0;
            for (int i = 0; i < nums.size(); ++i){
                vector<int> dp(nums[i] + 1);
                dp[0] = 1;
                for (int j = 0; j < k; ++j){
                    int l = queries[j][0];
                    int r = queries[j][1];
                    int v = queries[j][2];
                    int x = i >= l && i <= r ? v : 0;
                    for (int j = nums[i]; j >= x; --j){
                        if (dp[j - x]){
                            dp[j] = 1;
                        }
                    }
                }
                if (dp[nums[i]]){
                    g |= 1<<i;
                }
            }
            if (g == (1 << n) - 1){
                return true;
            }
            return false;
        };
        int m = queries.size();
        int lt = 0;
        int rt = m + 1;
        while (lt < rt - 1){
            int mid = (lt + rt) / 2;
            if (check(mid)){
                rt = mid;
            }
            else{
                lt = mid;
            }
        }

        return rt == m + 1 ? -1 : rt;
    }
};

